9)		{			$abc="4.00";			return $abc;		}		else if($ru>=80 && $ru<=100)		{			$abc="5.00";			return $abc;		}			}	$ac=point($rua);	$ac1=point($ru1);	$ac2=point($ru2);	$ac3=point($ru3);	$ac4=point($ru4);	$ac5=point($ru5);	$ac6=point($ru6);	$ac7=point($ru7);	$ac8=point($ru8);	if(($ac>=2.00 && $ac<=5.00)&&($ac1>=2.00 && $ac1<=5.00)&&($ac2>=2.00 && $ac2<=5.00)&&($ac3>=2.00 && $ac3<=5.00)&&($ac4>=2.00 && $ac4<=5.00)&&($ac5>=2.00 && $ac5<=5.00)&&($ac6>=2.00 && $ac6<=5.00)&&($ac7>=2.00 && $ac7<=5.00)&&($ac8>=2.00 && $ac8<=5.00))	{		$dolar=(($ac+$ac1+$ac2+$ac3+$ac4+$ac5+$ac6+$ac7+$ac8)/9);		$dolar=number_format($dolar,2); 	}	else	{		$dolar='Fail';	}	$d1=$ab;	$d2=$ab1;	$d3=$ab2;	$d4=$ab3;	$d5=$ab4;	$d6=$ab5;	$d7=$ab6;	$d8=$ab7;	$d9=$ab8;	$ad=mysql_connect("localhost","root","");	mysql_select_db("dusrasof",$ad);	$as="Insert into dolar14(`roll`,`board`,`Group`,`Session`,`Type`,`Center`,`Name`,`Father_Name`,`Mother_Name`,`Registration_No`,`Date_Of_Birth`,`Institute`,`Sub_Name1`,`Sub_Name2`,`Sub_Name3`,`Sub_Name4`,`Sub_Name5`,`Sub_Name6`,`Sub_Name7`,`Sub_Name8`,`Sub_Name9`,`Marks1`,`Marks2`,`Marks3`,`Marks4`,`Marks5`,`Marks6`,`Marks7`,`Marks8`,`Marks9`,`p1`,`p2`,`p3`,`p4`,`p5`,`p6`,`p7`,`p8`,`p9`,`Cod1`,`Cod2`,`Cod3`,`Cod4`,`Cod5`,`Cod6`,`Cod7`,`Cod8`,`Cod9`,`Total_GPA`)	values('$rolla','$board','$group','$session','$type','$center','$name','$fname','$mname','$reg','$db','$institute','$sub1','$sub2','$sub3','$sub4','$sub5','$sub6','$sub7','$sub8','$sub9','$rua','$ru1','$ru2','$ru3','$ru4','$ru5','$ru6','$ru7','$ru8','$d1','$d2','$d3','$d4','$d5','$d6','$d7','$d8','$d9','$co1','$co2','$co3','$co4','$co5','$co6','$co7','$co8','$co9','$dolar')	";	mysql_query($as,$ad);	?><html>	<head>		<link rel="stylesheet"type="text/css"href="../css/output.css"/>	</head>	<body>		<div id="main">			<div id="head">						</div>			<div id="head1">				<h2>SSC/Dhakil/Equivalent Result Publication 2012 </h2><hr/>			</div>			<div id="head2">				<table>